salesOrderSummaryList = [] ordersTotal = [dateKey:"Prior Total"] for (def dateBucket in sr.getAggregations().get("DateOuter").getBuckets()) { def prodAgg = dateBucket.getAggregations().get("ItemProduct") def discAgg = dateBucket.getAggregations().get("ItemDiscount") def newCustAgg = dateBucket.getAggregations().get("NewCustomer") BigDecimal orderCount = new BigDecimal(dateBucket.getAggregations().get("orderCount").getValue()) BigDecimal newCustomerOrderCount = new BigDecimal(newCustAgg.getAggregations().get("newCustomerOrderCount").getValue()) BigDecimal productSaleTotal = new BigDecimal(prodAgg.getAggregations().get("productSaleTotal").getValue()) BigDecimal productIssuedTotal = new BigDecimal(prodAgg.getAggregations().get("productIssuedTotal").getValue()) BigDecimal productCostTotal = new BigDecimal(prodAgg.getAggregations().get("productCostTotal").getValue()) BigDecimal discountTotal = new BigDecimal(discAgg.getAggregations().get("discountTotal").getValue()) Map curLine = [dateKey:dateBucket.getKeyAsString(), orderCount:orderCount, newCustomerOrderCount:newCustomerOrderCount, newCustomerPercent:(orderCount ? (newCustomerOrderCount/orderCount) : 0.0), totalSales:new BigDecimal(dateBucket.getAggregations().get("totalSales").getValue()), productSaleTotal:productSaleTotal, productIssuedTotal:productIssuedTotal, productCostTotal:productCostTotal, productQuantityTotal:new BigDecimal(prodAgg.getAggregations().get("productQuantityTotal").getValue()), discountTotal:discountTotal, netSales:(productSaleTotal + discountTotal), discountPercent:(productSaleTotal ? (-discountTotal/productSaleTotal) : 0.0), costPercent:(productIssuedTotal ? (productCostTotal/productIssuedTotal) : 0.0), orderCountMa:0.0, newCustomerOrderCountMa:0.0, newCustomerPercentMa:0.0, productQuantityTotalMa:0.0, productSaleTotalMa:0.0, discountPercentMa:0.0, netSalesMa:0.0] salesOrderSummaryList.add(curLine) // don't include the first (most recent) period in the total and average if (salesOrderSummaryList.size() > 1) addBigDecimalsInMap(ordersTotal, curLine) }
// get the this, last, prior periods before reversing list ordersThis = salesOrderSummaryList.size() > 0 ? salesOrderSummaryList[0] : null ordersLast = salesOrderSummaryList.size() > 1 ? salesOrderSummaryList[1] : null ordersPrior = salesOrderSummaryList.size() > 2 ? salesOrderSummaryList[2] : null
// reverse the list (comes back from ES newest first) salesOrderSummaryList.reverse(true)
// calculate moving averages if (maInt > 0 && salesOrderSummaryList.size() > maInt) { BigDecimal maBd = new BigDecimal(maInt) // NOTE: buckets originally come back newest to oldest so for MA reverse, then reverse after List originalList = new ArrayList(salesOrderSummaryList) // trim list for maInt so it has only display periods salesOrderSummaryList = new ArrayList(originalList.subList(maInt, originalList.size())) for (int idx = 0; idx < salesOrderSummaryList.size(); idx++) { Map curLine = (Map) salesOrderSummaryList.get(idx) List lookbackList = originalList.subList(idx, idx + maInt) curLine.orderCountMa = lookbackList*.orderCount.sum() / maBd curLine.newCustomerOrderCountMa = lookbackList*.newCustomerOrderCount.sum() / maBd curLine.newCustomerPercentMa = lookbackList*.newCustomerPercent.sum() / maBd curLine.productQuantityTotalMa = lookbackList*.productQuantityTotal.sum() / maBd curLine.productSaleTotalMa = lookbackList*.productSaleTotal.sum() / maBd curLine.discountPercentMa = lookbackList*.discountPercent.sum() / maBd curLine.netSalesMa = lookbackList*.netSales.sum() / maBd } }
ordersTotal.newCustomerPercent = null ordersTotal.discountPercent = null ordersTotal.costPercent = null
BigDecimal avgCount = new BigDecimal(salesOrderSummaryList.size()) ordersAverage = new LinkedHashMap(ordersTotal) divideBigDecimalsInMap(ordersAverage, avgCount) ordersAverage.dateKey = "Prior Average" ordersAverage.newCustomerPercent = (ordersAverage.orderCount ? (ordersAverage.newCustomerOrderCount/ordersAverage.orderCount) : 0.0) ordersAverage.discountPercent = (ordersAverage.productSaleTotal ? (-ordersAverage.discountTotal/ordersAverage.productSaleTotal) : 0.0) ordersAverage.costPercent = (ordersAverage.productIssuedTotal ? (ordersAverage.productCostTotal/ordersAverage.productIssuedTotal) : 0.0)
salesOrderSummaryList.add(ordersAverage) salesOrderSummaryList.add(ordersTotal)

